import React, { useState, useEffect } from "react";
import "./App.css"; // Make sure the wave CSS is imported here

const Home = () => {
  const [gradient, setGradient] = useState("");

  useEffect(() => {
    const hour = new Date().getHours();
    if (hour >= 6 && hour < 12) {
      setGradient(
        "linear-gradient(45deg, #ff7e5f 20%, #feb47b 40%, #f0e68c 60%), linear-gradient(135deg, #ffcc99 10%, #f0e68c 40%)" // Morning
      );
    } else if (hour >= 12 && hour < 18) {
      setGradient(
        "linear-gradient(45deg, #87cefa 30%, #4682b4 60%), linear-gradient(135deg, #ff6347 15%, #ff4500 50%)" // Afternoon
      );
    } else {
      setGradient(
        "linear-gradient(45deg, #2f4f4f 20%, #4682b4 60%), linear-gradient(135deg, #8a2be2 10%, #2f4f4f 50%)" // Night
      );
    }
  }, []);

  return (
    <div
      style={{
        background: gradient,
        position: "relative",
        overflow: "hidden",
        minHeight: "100vh", // Ensure the container takes up the full viewport height
      }}
    >
      {/* Add the waves */}
      <div className="wave-container">
        <div className="wave"></div>
        <div className="wave2"></div>
        <div className="wave3"></div>
      </div>

      <div className="container text-center mt-5">
        <h1>Welcome to the Home Page</h1>
        <button>Click Me</button>
      </div>
    </div>
  );
};

export default Home;
